import React, { useState } from "react";
import axios from "axios";
import Weatherinfo from "./Weatherinfo";
import weatherforecast from "./weatherforecast";

export default function Weather(props) {
  const [city, setCity] = useState(props.defaultCity);
  let [load, setLoad] = useState(false);
  let [weatherData, setWeatherData] = useState({});

  function handleSubmit(event) {
    event.preventDefault();
    let apiKey = `cb957f6011cbe58c2051d26f28640ef9`;
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(url).then(showTemperature);
  }
  function showTemperature(response) {
    setLoad(true);
    setWeatherData({
      coordinates: response.data.coordinates,
      temperature: response.data.main.temp,
      description: response.data.weather[0].description,
      humidity: response.data.main.humidity,
      wind: Math.round(response.data.wind.speed),
      icon: `http://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
    });
  }

  function updateCity(event) {
    setCity(event.target.value);
  }

  let form = (
    <form onSubmit={handleSubmit}>
      <input
        onChange={updateCity}
        type="Search"
        placeholder="Search for a city"
      />
      <input type="submit" value="Search" />
    </form>
  );

  if (load) {
    return (
      <div>
        {" "}
        {form} <br />{" "}
        <ul className="weather">
          {" "}
          <li> Here's the weather in {city} </li>{" "}
          <li>
            {" "}
            <img src={weatherData.icon} alt={weatherData.description} />{" "}
          </li>
          <br /> <strong>Temperature:</strong>{" "}
          {Math.round(weatherData.temperature)} ÂºC
          <li>
            {" "}
            <strong>Description:</strong> {weatherData.description}{" "}
          </li>{" "}
          <li>
            <strong>Humidity:</strong> {Math.round(weatherData.humidity)} %
          </li>
          <li>
            <strong>Wind:</strong> {weatherData.wind} km/h
          </li>
        </ul>
        <Weatherinfo data={weatherData} />
        <weatherforecast coordinates={weatherData.coordinates} />
      </div>
    );
  } else {
    return <div> {form} </div>;
  }
}
